//functions
void lcd_begin(){
  pinMode(sce3, OUTPUT);
  pinMode(rst4, OUTPUT);
  pinMode(dc5, OUTPUT);
  pinMode(dn6, OUTPUT);
  pinMode(sclk7, OUTPUT);
  pinMode(led8, OUTPUT);

  SPI.begin();
  SPI.setDataMode(SPI_MODE0);
  SPI.setBitOrder(MSBFIRST);

  //initialize
  digitalWrite(rst4, LOW);
  //delay(50);
  digitalWrite(rst4, HIGH);

  digitalWrite(dc5, 0);
  digitalWrite(sce3, LOW);
  SPI.transfer(0x21);
  SPI.transfer(0xB0);
  SPI.transfer(0x04);
  SPI.transfer(0x14);
  SPI.transfer(0x20);
  SPI.transfer(0x0c);
  //SPI.transfer(0x21);
  SPI.transfer(0x00);
  digitalWrite(sce3, HIGH);
};

void clearScreen(){
  for(int i = 0; i < (84*48)/8; i++) {
    digitalWrite(dc5, HIGH);
    digitalWrite(sce3, LOW);
    SPI.transfer(0x00);
    //Serial.println(over[i]);
    digitalWrite(sce3, HIGH);
  }
};

void XYPosition(int x, int y) {
  clearScreen();
  digitalWrite(dc5, LOW);
  digitalWrite(sce3, LOW);
  SPI.transfer(0x80);
  SPI.transfer(0x40);
  digitalWrite(sce3, HIGH);
};

void over15Display() {
  XYPosition(0,0);
  clearScreen();
  for (int i = 0; i < (84*48)/8; i++) {
    digitalWrite(dc5, HIGH);
    digitalWrite(sce3, LOW);
    SPI.transfer(over[i]);
    //Serial.println(over[i]);
    digitalWrite(sce3, HIGH);
  }
};

